def replace_text(string):
    print(string)
    print('------------------------------------------------------------')
    pattern_lang = r"```(.*?)\n"
    pattern_tag = r"```.+\n"

    match = re.search(pattern_lang, string)
    if match:
        result = match.group(1)
        new_string = re.sub(pattern_tag, f'<pre language="{result}">\n', string)
        print(new_string.replace('```', '</pre>'))
        return new_string.replace('```', '</pre>')
    else:
        return string

